import React, {useState} from 'react';
import styled from 'styled-components';

export const Error = styled.div`
	position: fixed;
	bottom: 0%;
	z-index: 12;
	width: 75vw;
	left: 50%;
	transform: translate(-50%,0%);
	background-color: rgba(210,30,30,1.0);
	color: white;
	font-weight: bold;
	text-align: center;
	padding-top: 1%;
	padding-bottom: 1%;
	border-bottom: none;
	border-radius: 30px 30px 0px 0px;
`;
