import React, { useState, useEffect } from "react";
import { Form, FormControl } from "react-bootstrap";
import styled from "styled-components/macro";
import { createGlobalStyle } from "styled-components";
import { SessionBtn } from "./sessionbtn";
import { FilterDetails } from "./filterdetails";
import { VideoPlayer } from "./videoplayer";
import { Loader } from "./spinner.jsx";

export const GlobalTheme = createGlobalStyle`
body {
	background-color: rgba(60,60,60,255) !important;
}`;

const ViewportContainer = styled.div`
	grid-row:2/3;
	grid-column:2/3;
	display:grid;
	grid-template-columns: 100vw;
	grid-template-rows: 5vh 95vh;
`;

const Viewport = styled.div`
	height: 100%;
	color: white;
	font-weight: bolder;
	grid-row:2/3;
	grid-column:1/2;
	display: grid;
	grid-gap: 5%;
	grid-template-columns: repeat(12, 1fr);
	grid-auto-rows: minmax(25px, auto);
	padding-left: 5%;
	padding-right: 5%;
	padding-top: 5%;
	padding-bottom: 5%;
	/*border: 2px solid blue;*/
`;

const TabbarContainer = styled.div`
	grid-row: 1 / 2;
	grid-column: 1 / 1;
	display: flex;
	width: 100%;
	height: 5vh;
	box-shadow: 0px 3px 20px rgba(0,0,0,0.5);
`

const Tab = styled.div`
	/*border: 3px solid red;*/
	text-align: center;
	vertical-align:middle;
	line-height:5vh;
	color: white;
	font-weight: bold;
	background-color: rgba(30,30,30,1);
	transition: all 0.1s ease-out;
	flex: 1;
	z-index:9;
	/*border-radius: 8px 8px 8px 8px;*/
	&:hover {
	background-color: rgba(90,90,90,1);
	}
`

const DocumentCard = styled.div`
	position: relative;
	/*border: 3px solid yellow;*/
`;

const ParamSlider = styled.input`
	-webkit-appearance: none;
	appearance: none;
	min-width: 100px;
	width: 100%;
	height: 12px;
	background: #061223;
	outline: none;
	opacity: 0.8;
	-webkit-transition: 0.2s;
	transition: opacity 0.2s;
	border-radius: 2px;
	box-shadow: 1.5px 5px 10px rgba(0, 0, 0, 0.3);
`;

const Tabbar = (props) => {
	const [tabSelected,setTabSelected] = useState(0);

	const selectTab = (e) => {
		setTabSelected(e.currentTarget.attributes.id);
		console.log(e.currentTarget.attributes.id);
	}

	return (
		<TabbarContainer>
			<Tab id="0">Upload Image</Tab>
			<Tab id="1" onClick={selectTab}>Create Pipeline</Tab>
			<Tab id="2">Editor</Tab>
		</TabbarContainer>	
	);
};

export const AppViewport = (props) => {
	const [showVP, setShowVP] = useState(false);
	const [amountFrames, setAmountFrames] = useState(0);

	const tellAmountFrames = (amount) => {
		setAmountFrames(amount);
		console.log("Amount of filter API calls: "+amountFrames);
	}
	return (
		<>
		<ViewportContainer>
			<Tabbar/>
			<Viewport>
				<SessionBtn onClick={e => {setShowVP(true)}}/>
				{ showVP && props.fetchFinished ?
				<>
					<p>
						<FilterDetails fncTellAmountFrames={tellAmountFrames} amountFrames={amountFrames}/>
					</p>
				</>
				: null }
				<>
					<p>
						<DocumentCard>
							{ amountFrames>5 ?
							<VideoPlayer streamKey={props.streamKey}/>
							: <Loader/> }
						</DocumentCard>
					</p>
				</>
			</Viewport>
		</ViewportContainer>
		</>
	);
};
