import React from "react";
import { Form, FormControl } from "react-bootstrap";
import styled from "styled-components";
import { createGlobalStyle } from "styled-components";

export const GlobalTheme = createGlobalStyle`
body {
  background-color: rgba(60,60,60,255) !important;
}`;

const Viewport = styled.div`
  color: white;
  font-weight: bolder;
  position: absolute;
  display: grid;
  grid-gap: 5%;
  grid-template-columns: repeat(12, 1fr);
  grid-auto-rows: minmax(25px, auto);
  left: 50%;
  top: 50%;
  padding-left: 5%;
  padding-right: 5%;
  padding-top: 5%;
  padding-bottom: 5%;
  transform: translate(-50%, -50%);
  border: 8px solid red;
`;

const DocumentCard = styled.div`
  position: relative;
  border: 3px solid yellow;
`;

const VpImage = styled.img`
  padding-left: 5%;
  padding-right: 5%;
  padding-top: 5%;
  padding-bottom: 5%;
  border: 5px solid rgba(30, 30, 30, 255);
  border-radius: 8px;
  box-shadow: 4px 1px 24px rgba(0, 0, 0, 25%);
`;

const ParamSlider = styled.input`
overflow: hidden;
display: block;
appearance: none;
max-width: 700px;
width: 100%;
margin: 0;
cursor: pointer;

&:focus {
  outline: none;
`;

export const AppViewport = (props) => (
  <Viewport>
    <p>
      <ParamSlider type="range" />
    </p>
    <p>
      <DocumentCard>
        <VpImage src={props.imgsrc} />
      </DocumentCard>
    </p>
  </Viewport>
);
