import React, {useState,useRef} from "react";
import styled from "styled-components";
import AddIcon from '@material-ui/icons/Add';
import { Error } from "./error";
import { Loader } from "./spinner";	// this can go after icon integrated

const DropzoneContainerStyled = styled.div`
	width: 100%;
	height: 100%;
	margin: 0 auto;
	display: flex;
	flex-direction: column;
	text-align: center;
`;

/*
background: ${props => props.submitting ? 
		'linear-gradient(to right, #21a7eb 0%, #21a7eb 50%, rgba(195,195,195,0.6) 50%)'
		: props.dragged ? 
			'#21a7eb' : 'rgba(255,255,255,0.25)'};
*/

const DropzoneStyled = styled.div`
	margin: 0 auto;
	flex: auto;
	width: 40%;
	max-height: 50%;
	display: flex;
	flex-direction: column;
	justify-content: center;
	align-items: center;
	background: ${props => props.submitting ? `linear-gradient(to right, #21a7eb 0%, #21a7eb ${props.progress ? props.progress:`0`}%, rgba(195,195,195,0.6) ${props.progress ? props.progress : `0`}%` : '#21a7eb'});
	${props => {
		if (!props.submitting) {
			if (props.dragged) return 'background-color: #21a7eb';
			return 'background-color: rgba(255,255,255,0.95); &:hover { background-color: rgba(255,255,255,0.95); }';
		}
	}};
	background-origin: border-box;
	border: 3px solid ${props => props.dragged ? 'rgba(30,30,30,0.8)' : 'rgba(195,195,195,0.6)'};
	border-style: dashed;
	border-radius: 60px;
	transition: all 0.5s ease,
		width 0.3s ease,
		height 0.3s ease,
		background-color 0.6s ease;
	&:hover {
		background: rgba(255,255,255,0.35);
	}
	@media (max-width: 768px) {
		width: 90%;
		max-height: 60%;
	}
	@media (max-height: 480px) {
		max-height: 90%;
	}
`;

const AddIconStyled = styled(AddIcon)`
	&& {
		font-size: 100%;
		margin-right: 5px;
	}
`;

const UploadIcon = styled.img`
	width: 33.3%;
	mix-blend-mode: color;
`;

const UploadButton = styled.button`
	display: flex;
	flex-direction: row;
	flex-wrap: wrap;
	align-items: center;
	justify-content: center;
	margin: auto;
	color: white;
	font-weight: bold;
	padding: 10px 15px 10px 15px;
	background-color: #061223;
	border: none;
	border-radius: 80px;
	box-shadow: 0px 5px 16px rgba(0,0,0,0.4);
	transition: all 0.2s ease;
	&:hover {
		transform: scale(1.05);
		color: white;
		background-color: #21a7eb;
		box-shadow: 0px 3px 8px rgba(0,0,0,0.75);
	}
`;

export const Dropzone = (props) =>
{	
	const [dragged,setDragged] = useState(false);
	const [dropped,setDropped] = useState(false);
	const [submitting,setSubmitting] = useState(false); //this needs to be set to false after testing CloudUploadIcon animation.
	const [uploadProgress,setUploadProgress] = useState(0);

	const [errorVisible,setErrorVisible] = useState(false);
	const [errorMsg,setErrorMsg] = useState("Undefined Error.");

	const dropHandler = async (e) => {
		e.preventDefault();
		e.stopPropagation();
		setDragged(false);
		setDropped(true);

		if (e.dataTransfer.items && e.dataTransfer.items[0].kind === 'file') {
			inputFile = e.dataTransfer.items[0].getAsFile();
			console.log("File name: "+inputFile.name);
			if (inputFile.type === 'image/jpeg') {
				setErrorVisible(false);
				setSubmitting(true);
				await fetch('app.rastered.io/push', { 
					method:'POST',
					credentials:'include',
					body:inputFile
				});
				setSubmitting(false);
			}
			else {
				setErrorMsg("This file is not an image.");
				setErrorVisible(true);
				console.log("File type: "+inputFile.type);
			}
		};
	};

	const dragOverHandler = (e) => {
		e.preventDefault();
		e.stopPropagation();
		setDragged(true);
	};

	let inputFile = useRef(null);

	const handleSubmit = async (e) => {
		if (e.target.files[0] && e.target.files[0] !== null) {
			let file = e.target.files[0];
			console.log(file.name);
			if (file.type === 'image/jpeg') {
				setErrorVisible(false);
				setSubmitting(true);
				await fetch('app.rastered.io/push', { 
					method:'POST',
					credentials:'include',
					body:file
				});
				setSubmitting(false);
			}
			else {
				setErrorMsg("This file is not an image.");
				setErrorVisible(true);
				console.log("File type: "+file.type);
			}
		}
	};

	console.log(uploadProgress);
	const putTest = () =>
	{
		setSubmitting(true);
		const blob = new Blob([new Uint8Array(4*1024*1024)]);
		const xhr = new XMLHttpRequest();
		xhr.upload.onprogress = (e) => {
			if (e.lengthComputable) {
				setUploadProgress(Math.floor((e.loaded/e.total)*100));
			}
		};

		xhr.open("PUT","https://httpbin.org/put",true)
		xhr.setRequestHeader("Content-Type","application/octet-stream");
		xhr.send(blob);
	};

	return(
		<>
		<DropzoneContainerStyled>
			Pick the image you'd like to edit here.
			<DropzoneStyled 
				onDragOver={dragOverHandler} 
				onDrop={dropHandler} 
				onDragLeave={() => setDragged(false)} 
				dragged={dragged}
				submitting={submitting}
				progress={uploadProgress}
			>
				{ dragged || submitting ? null :
				<UploadButton onClick={() => inputFile.current.click()}>
					<AddIconStyled/> Choose File
				</UploadButton> }
				<UploadButton onClick={putTest}/>
				{ submitting ? <UploadIcon src="upload.svg"/> : null }
				<input type='file' id='file' ref={inputFile} onChange={handleSubmit} style={{display:'none'}}/>
			</DropzoneStyled>
		</DropzoneContainerStyled>
		{ errorVisible ?
			<Error msg={errorMsg} hide={() => setErrorVisible(false)}/>
		: null }
		</>
	);
};
