import React, {useState,useRef} from "react";
import styled from "styled-components";
import AddIcon from '@material-ui/icons/Add';
import { Error } from "./error";

const DropzoneContainerStyled = styled.div`
	width: 100%;
	height: 100%;
	margin: 0 auto;
	display: flex;
	flex-direction: column;
	text-align: center;
`;

const DropzoneStyled = styled.div`
	margin: 0 auto;
	flex: auto;
	width: 40%;
	max-height: 50%;
	display: flex;
	flex-direction: column;
	justify-content: center;
	align-items: center;
	background-color: ${props => props.dragged ? '#21a7eb' : 'rgba(255,255,255,0.25)'};
	border: 3px solid ${props => props.dragged ? 'rgba(30,30,30,0.8)' : 'rgba(195,195,195,0.6)'};
	border-style: dashed;
	border-radius: 60px;
	transition: all 0.5s ease,
		width 0.3s ease,
		height 0.3s ease,
		background-color 0.6s ease;
	&:hover {
		background-color: ${props => props.dragged ? '#21a7eb' : 'rgba(255,255,255,0.35)'};
	}
	@media (max-width: 768px) {
		width: 90%;
		max-height: 60%;
	}
	@media (max-height: 480px) {
		max-height: 90%;
	}
`;

const AddIconStyled = styled(AddIcon)`
	&& {
		font-size: 100%;
		margin-right: 5px;
	}
`;

const UploadButton = styled.button`
	display: flex;
	flex-direction: row;
	flex-wrap: wrap;
	align-items: center;
	justify-content: center;
	margin: auto;
	color: white;
	font-weight: bold;
	padding: 10px 15px 10px 15px;
	background-color: #061223;
	border: none;
	border-radius: 80px;
	box-shadow: 0px 5px 16px rgba(0,0,0,0.4);
	transition: all 0.2s ease;
	&:hover {
		transform: scale(1.05);
		color: white;
		background-color: #21a7eb;
		box-shadow: 0px 3px 8px rgba(0,0,0,0.75);
	}
`;

export const Dropzone = (props) =>
{
	const showError = (errorMsg) => {
		return(
			<Error msg={errorMsg}/>
		);
	};

	const [dragged,setDragged] = useState(false);
	const [dropped,setDropped] = useState(false);
	const [errorVisible,setErrorVisible] = useState(false);
	const [errorMsg,setErrorMsg] = useState("Undefined Error.");

	const dropHandler = (e) => {
		e.preventDefault();
		e.stopPropagation();
		setDragged(false);
		setDropped(true);

		if (e.dataTransfer.items && e.dataTransfer.items[0].kind === 'file') {
			inputFile = e.dataTransfer.items[0].getAsFile();
			console.log("File name: "+inputFile.name);
			if (inputFile.type === 'image/jpeg') {

			} 
			else {
				setErrorMsg("This file is not an image.");
				setErrorVisible(true);
				console.log("File type: "+inputFile.type);
			}
		};
	};

	const dragOverHandler = (e) => {
		e.preventDefault();
		e.stopPropagation();

		setDragged(true);
	};

	let inputFile = useRef(null);

	return(
		<DropzoneContainerStyled>
			Pick the image you'd like to edit here.
			<DropzoneStyled onDragOver={dragOverHandler} onDrop={dropHandler} onDragLeave={() => setDragged(false)} dragged={dragged}>
				{dragged ? null :
				<UploadButton onClick={() => inputFile.current.click()}>
					<AddIconStyled/> Choose File
				</UploadButton>}
				<input type='file' id='file' ref={inputFile} style={{display:'none'}}/>
			</DropzoneStyled>
			{/*<Error msg="The file you've chosen is not an image."/>*/}
		</DropzoneContainerStyled>
	);
};
