import React, {useState} from "react";
import styled from "styled-components";

const TopBarStyled = styled.div`
	height: 10vh;
	width: 100vw;
	z-index: 9;
	background-color: rgba(30,30,30,1.0);
	display: flex;
	flex-direction: row;
	align-items: center;

`;

const Logo = styled.img`
	max-height: 65%;
	transition: all 0.4s ease;
	padding-left: 10px;
	&:hover { 
		/*transform: rotate(1440deg);*/
		transform: rotate3d(0,1,0,1080deg);
	}
`;

const Slogan = styled.div`
	max-width: 25vw;
	color: white;
	font-style: italic;
	font-weight: bold;
	padding-left: 10px;
	font-size: calc(8px + 0.25vw);
	text-transform: uppercase;
`;

const MenuIconStyled = styled.div`
	display: none;
	flex-direction: column;
	margin-right: 20px;
	margin-left: auto;
	@media (max-width: 640px) {
		display: flex;
	}
`;

const MenuIconStroke = styled.div`
	margin: 2px 0px 2px 0px;
	width: 22px;
	height: 2px;
	border-radius: 3px;
	background-color: ${props => props.openMenu ? '#6fccfc' : 'white'};
`;

const LinkContainer = styled.div`
	display: flex;
	flex-direction: row;
	margin-left: 25px;
	@media (max-width: 768px) {
		margin-left: 20px;
	}
	@media (max-width: 640px) {
		display: none;
	}
`;

const Link = styled.a`
	text-decoration: none;
	font-weight: bold;
	color: white;
	margin-right: 25px;
	@media (max-width: 768px) {
		margin-right: 10px;
	}
	&:hover {
		color: #6fccfc;
	}
`;

const Name = styled.a`
	font-size: calc(16px + 1vw);
	text-decoration: none;
	color: white;
	text-shadow: 5px 4px 0px rgba(0,0,0,1.0);
	font-weight: bold;
	transition: all 0.3s ease;
	&:hover {
		color: #6fccfc;
	}
`;

const MenuIcon = (props) =>
{
	return(
		<MenuIconStyled onMouseEnter={() => props.handleMouse(true)} onMouseLeave={() => props.handleMouse(false)} >
			<MenuIconStroke openMenu={props.openMenu}/>
			<MenuIconStroke openMenu={props.openMenu}/>
			<MenuIconStroke openMenu={props.openMenu}/>
		</MenuIconStyled>
	);
};

export const TopBar = (props) => {
	const [openMenu,setOpenMenu] = useState(false);

	return(
		<TopBarStyled>
			<Logo src="rio-logo.png"/>
			<Slogan>Image Processing made simple.</Slogan>
			<LinkContainer>
				<Link href="/">Home</Link>
				<Link href="/">About</Link>
				<Link href="/">Privacy</Link>
			</LinkContainer>
			<MenuIcon handleMouse={(value) => setOpenMenu(value)} openMenu={openMenu}/>
			<Menu/>
			{/*<Name href="https://rastered.io/">rastered.io</Name>*/}
		</TopBarStyled>
	);
};
