import React, {useState,useRef} from "react";
import styled from "styled-components";

const DropzoneContainerStyled = styled.div`
	width: 80vw;
	height: 80vh;
	margin: 0 auto;
	text-align: center;
`;

const DropzoneStyled = styled.div`
	width: 75%;
	height: 70%;
	margin: 0 auto;
	display: flex;
	justify-content: center;
	align-items: center;
	background-color: ${props => props.dragged ? '#21a7eb' : 'rgba(255,255,255,0.25)'};
	border: 3px solid ${props => props.dragged ? 'rgba(30,30,30,0.8)' : 'rgba(195,195,195,0.6)'};
	border-style: dashed;
	border-radius: 60px;
	transition: all 0.5s ease,
		background-color 0.6s ease;
	&:hover {
		background-color: ${props => props.dragged ? '#21a7eb' : 'rgba(255,255,255,0.35)'};
	}
`;

const UploadButton = styled.button`
	color: white;
	font-weight: bold;
	padding: 10px 20px 10px 20px;
	background-color: #061223;
	border: none;
	border-radius: 80px;
	box-shadow: 0px 5px 16px rgba(0,0,0,0.4);
	transition: all 0.2s ease;
	&:hover {
		transform: scale(1.05);
		color: white;
		background-color: #21a7eb;
		box-shadow: 0px 3px 8px rgba(0,0,0,0.75);
	}
`;

export const Dropzone = (props) =>
{
	const [dragged,setDragged] = useState(false);

	const dropHandler = (e) => {
		e.preventDefault();
		e.stopPropagation();
		setDragged(false);

		if (e.dataTransfer.items && e.dataTransfer.items[0].kind === 'file') {
			inputFile = e.dataTransfer.items[0].getAsFile();
			console.log("File name: "+inputFile.name);
		};
	};

	const dragOverHandler = (e) => {
		e.preventDefault();
		e.stopPropagation();

		setDragged(true);
	};


	let inputFile = useRef(null);
	return(
		<DropzoneContainerStyled>
			Pick the image you'd like to edit here.
			<DropzoneStyled onDragOver={dragOverHandler} onDrop={dropHandler} onDragLeave={() => setDragged(false)} dragged={dragged}>
				{dragged ? null :
				<UploadButton onClick={() => inputFile.current.click()}>
					Choose File
				</UploadButton>}
				<input type='file' id='file' ref={inputFile} style={{display:'none'}}/>
			</DropzoneStyled>
		</DropzoneContainerStyled>
	);
};
